import { useState } from "react";
import ButtonComponent from "../Button/ButtonComponent";
import styles from './PopUpComponent.module.css'
import CardComponent from "../CardComponent/CardComponent";

export function PopUpComponent(props){
    const [currentClassname, setCurrentClassname] = useState('hidden');

    if(props.errorMessages > 0){
        setCurrentClassname('card');
        props.errorMessages.length = 0;
    }

    const buttonClickedHandler = ()=>{
        setCurrentClassname('hidden');
    }


    return(

        <CardComponent className= {currentClassname}>
        <h2 className={styles.popupTitle}>TITLE</h2>
                <ul>
                {
                    props.errorMessages.map(
                        (errorMessage)=>{return (<li>{errorMessage}</li>)
                    })
                }

                </ul>
                <ButtonComponent type='none' onButtonClicked={buttonClickedHandler} additionalStyles={styles.popupButton}>Okay</ButtonComponent>
        </CardComponent>

    )
}


export default PopUpComponent;
